mapper.update:
update qreply
set content = #{content}
where rnum = #{rnum}

mapper.select:
select rnum, content, regdate, nicname, qano, r
	FROM(
	select rnum, content, regdate, nicname, qano, rownum r
	    FROM(
	        select rnum, content, regdate, nicname, qano
	        from QREPLY
	        where qano = #{qano}
	        order by rnum DESC
	        )
	    )
	
	    WHERE r >= #{sno} and r <= #{eno}
