mapper.update:
		update bbs
		set content=#{content}
		,code=#{code}
		,subject=#{subject}

		
			,filename=#{filename}
		

		where bbsno = #{bbsno}

mapper.select:
		select bbsno,code,subject,viewcnt,bdate,nicname,filename,r
		from (
		select bbsno,code,subject,viewcnt,bdate,nicname,filename,rownum as r
		from(
		select bbsno,code,subject,viewcnt,bdate,nicname,filename
		from bbs

		
			
				
					subject like '%'||#{word}||'%'
				
				
					nicname like '%'||#{word}||'%'
				
				
					code like '%'||#{word}||'%'
				
			
		

		order by bbsno desc
		)
		)



where r>=#{sno} and r<=#{eno}
