mapper.update:
update nreply
set content=#{content}
where nreplyno= #{nreplyno}

mapper.select:
		select nreplyno, content,nreplydate,nicname,n_no, r
		from(
		select nreplyno, content, nicname,nreplydate, n_no, rownum as r
		from(
		select nreplyno, content, nicname,nreplydate, n_no from nreply
		where n_no = #{n_no}
		order by nreplyno DESC
		) 
                                                        
    )where r>=#{sno} and r<=#{eno}
